mapper.select:
select * from ( 
select rownum rnum, B.* from(
select * from productcolor order by pco_no desc
) B	order by rnum
) R	where rnum between #{startNo} and #{endNo}

select * from ( 
select rownum rnum, B.* from(
select * from productcolor 
where pco_name like '%' || #{word} || '%'
order by pco_no desc
) B	order by rnum
) R	where rnum between #{startNo} and #{endNo}
